mapper.select:
SELECT * FROM tbl_text WHERE m_reader=#{m_reader} AND NOT m_sender=#{m_sender} AND t_read_del = 'N' 
ORDER BY t_send_date DESC 
limit #{pageStart}, #{perPageNum}

SELECT * FROM tbl_text WHERE m_sender=#{m_sender} AND NOT m_reader=? AND t_send_del = 'N'
ORDER BY t_send_date DESC
limit #{pageStart}, #{perPageNum}

SELECT * FROM tbl_text WHERE m_reader=#{m_reader} AND m_sender =#{m_sender}
ORDER BY t_send_date DESC
limit #{pageStart}, #{perPageNum}
