mapper.select:
select r_regdate, r_content, r_point, m_giver, m_haver, 
(select m_name from tbl_member where m_id = m_giver) as m_givername, 
(select m_name from tbl_member where m_id = m_haver) as m_havername  
from tbl_record where ( m_giver = #{m_id} or m_haver = #{m_id})  
 
order by r_regdate desc 
limit #{pageStart}, #{perPageNum}

select count(r_regdate)	
from tbl_record 
where m_giver = #{m_id} or m_haver = #{m_id}

select c.c_num, c.c_name, c.c_agency, c.c_point, cm.cm_iscomplete, cm.cm_completedate, cm.cm_regda1te, cm.cm_image 
from tbl_certificate c left outer join tbl_certi_member cm on c.c_num = cm.c_num  and cm.m_id = #{m_id} 

order by c.c_num desc 
limit #{pageStart}, #{perPageNum}

SELECT sido, gugun, dong, bunji, zipcode FROM tbl_zip 
where sido = #{sido} and gugun = #{gugun} 
and dong like concat('%', #{dong}, '%') group by dong

  		select c.c_num, c.c_name, c.c_agency, c.c_point, cm.cm_iscomplete, cm.cm_completedate, cm.cm_regdate, cm.cm_image 
		from tbl_certificate c left outer join tbl_certi_member cm on c.c_num = cm.c_num 
		and cm.m_id = #{m_id} 
  		
  		order by cm.cm_regdate desc 
		limit #{pageStart}, #{perPageNum}

  		select count(c.c_num) 
		from tbl_certificate c left outer join tbl_certi_member cm on c.c_num = cm.c_num 
		and cm.m_id = #{m_id}

	SELECT l.l_num,l.l_time,l.l_day,l.l_name,l.l_teacher,l.l_grade,l.l_term,
    l.l_credit,l.l_room,l.l_ismust,l.l_dept,t.m_id,t.tt_grade 
	from tbl_lesson l inner join tbl_timetable t 
	on l.l_num = t.l_num 
	and t.m_id = #{m_id} 
	and t.tt_grade = #{tt_grade} 
	and t.tt_term = #{tt_term}  
	order by l.l_num desc

SELECT l.l_num,l.l_time,l.l_day,l.l_name,l.l_teacher,l.l_grade,l.l_term, 
l.l_credit,l.l_room,l.l_ismust,l.l_dept,t.m_id,t.tt_grade 
from tbl_lesson l left outer join tbl_timetable t 
on l.l_num = t.l_num 
and t.m_id = #{m_id}  
and t.tt_grade = #{tt_grade} 
where l.l_grade <= #{tt_grade} and l.l_term = #{tt_term} 

order by l.l_num desc 
limit #{pageStart}, #{perPageNum}

  		SELECT count(*)
		from tbl_lesson l left outer join tbl_timetable t 
		on l.l_num = t.l_num 
		and t.m_id = #{m_id}   
		and t.tt_grade =  #{tt_grade} 
		where l.l_grade <= #{tt_grade} and l.l_term = #{tt_term}

SELECT l_num,l_time,l_day,l_name,l_teacher,l_grade,l_term,l_credit,l_room,l_ismust,l_dept 
from tbl_lesson 

limit #{pageStart}, #{perPageNum}

